    const handleHam = () => {
        ham===false?setHam(true):setHam(false);
        console.log(ham);
    }
    const handleStream = (action) => {
        setStream(action)
    }

    return <div className="header container-fluid" style={{zIndex:"2",backgroundColor:{}}}>
    <div className="row">
        <div className="header-logo col-lg-1 col-md-1 col-sm-1">
            <img className="header-logo-img" src="https://www.freelogoservices.com/api/main/images/1j+ojFVDOMkX9Wytexe43D6kifWHqx5OmhzNwXs1M3EMoAJtlSkogvpu...fw7" />
    </div>
    <div className="col-lg-11 col-md-10 col-sm-11 head-items-colect">
        <h3 style={{display:"inline",position:"absolute",top:"17px",left:"197px"}}>
        
           <span className="header-items active" style={{textDecoration:"none"}}>Home</span>
            <span className="header-items" onMouseOver={(e) => handleStream(true)} onClick={(e)=> handleStream(true)}><Link to=""></Link>X Stream <i class="fas fa-chevron-down fa-0.5x"></i></span>
            <span className="header-items" onMouseOver={(e) => handleStream(true)}>News<i class="fas fa-chevron-down fa-0.5x"></i></span>
            <span className="header-items">Blog<i class="fas fa-chevron-down fa-0.5x"></i></span>
            <span className="header-items">About</span>
           
        </h3>
        <div className="hamburger" onClick={e=>handleHam()}>
            <div className="hamburger-lines"></div>
            <div className="hamburger-lines"></div>
            <div className="hamburger-lines"></div>
        </div>
        <button class="button-head "><span>Get Started </span></button>
    </div>
    <div>{ stream ? <DropDown 
