import React, { useState } from "react"
import "./cardsignup.css"
import leftsign from "./leftsign.PNG";
import rightsign from "./rightsign.PNG";
function CardSignup() {
    const [signme,setsignme]=useState(0);
    const [name,setname]=useState({fname:"",lname:""})
    return <div className="card-sign">
        <h3 className="cardsign-title">{signme==0?" Hi üñê What's your name ?" :signme===1 && !name.fname?
        "Hey "+ name.fname + "<br/>"+"Enter your E-mail":"Thank You "+name.fname + "üëçüëç"
        }</h3>
        <div className="cardsign-text" >
            <input type="text" placeholder="First Name"/>
            <input type="text" placeholder="Last Name"/>
        </div>
        {signme===0? <button type="button" onClick={()=>setsignme(1)} className="btn btn-info btn-card-sign">Sign Me !</button>:
        <button type="button" onClick={()=>setsignme(2)} className="btn btn-info btn-card-sign">Email Me !</button>}
        <div className="sign-img">
            <img  className="leftsign" src={leftsign} />
            <img className="rightsign" src = {rightsign}/>
        </div>
    </div>
}
export default CardSignup;
