import React, { Component } from "react"

class SearchBar extends Component {
    state = {term :""};
    onInputChange = (event) => {
        this.setState({term:event.target.value})

    }
    onFormSubmit = (event) => {
        event.preventDefault();
        this.props.onsubmit(this.state.term);
    }
    render () {
        return (
    <div className="input-group youtube-box">
            
    <input type="text" value={this.state.term} onChange={this.onInputChange} className="form-control"/>
    <div className="input-group-append">
      <span onClick={()=>this.onFormSubmit()} className="input-group-text search--icon">üîç</span>
      
    </div>
    </div>

    
        )
    }
}

export default SearchBar

